package com.scriptofan.ecommerce.Ebay.InventoryLocation;

import com.scriptofan.ecommerce.Ebay.InventoryLocation.Entity.LocationWrapper;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RestController;

@RestController
@RequestMapping
public class InventoryLocationController {

    private static final String DUMMY_USER_AUTH_TOKEN = "v^1.1#i^1#f^0#I^3#r^0#p^3#t^";

    private InventoryLocationService inventoryLocationService;

    public InventoryLocationController() {
        inventoryLocationService = new InventoryLocationService();
    }

    @GetMapping("/inventory-locations")
    public LocationWrapper[] getInventoryLocations() {
        return inventoryLocationService.getInventoryLocations(DUMMY_USER_AUTH_TOKEN);
    }
}
